import {
    handleLoadingSpinnerClose,
    handleLoadingSpinnerOpenWithOptions
} from "../../../common/ReduxBased/LoadingSpinner/actions";
import {
    LOGIN_PAGE_ERROR_HANDLER,
    LOGIN_SUBMIT_FORM_HANDLER
} from "../../../Login/actions";
import ls from 'local-storage'

import { reverseString } from "../../../common/Helpers";
import instance, { BASE_URL, BASE_URL_SQL } from "../../../../api/config"
export const JD_JOB_SUMMARY_TEXT_CHANGE_HANDLER = "JD_JOB_SUMMARY_TEXT_CHANGE_HANDLER";
export const JD_JOB_SUMMARY_EDITOR_ACTIVE_TEXT_HANDLER = "JD_JOB_SUMMARY_EDITOR_ACTIVE_TEXT_HANDLER";
export const JD_JOB_SUMMARY_EDITOR_TEXT_HANDLER = "JD_JOB_SUMMARY_EDITOR_TEXT_HANDLER";
export const JD_JOB_SUMMARY_EDITOR_TEXT_AS_HTML_HANDLER = "JD_JOB_SUMMARY_EDITOR_TEXT_AS_HTML_HANDLER";
export const JD_TEMPLATE_SAVE_HANDLER = "JD_TEMPLATE_SAVE_HANDLER";
export const JD_TEMPLATE_SAVE_SUMMARY_SUGGESTION_HANDLER = "JD_TEMPLATE_SAVE_SUMMARY_SUGGESTION_HANDLER";
export const JD_TEMPLATE_SAVE_SUMMARY_SHOW_LOGIN_POPUP_HANDLER = "JD_TEMPLATE_SAVE_SUMMARY_SHOW_LOGIN_POPUP_HANDLER";

export const textFieldChangeHandler = (name, value) => dispatch => {
    dispatch({
        type: JD_JOB_SUMMARY_TEXT_CHANGE_HANDLER,
        payload: value,
        key: name
    });
};

export const summaryDataActiveHandler = (value) => dispatch => {
    dispatch({
        type: JD_JOB_SUMMARY_EDITOR_ACTIVE_TEXT_HANDLER,
        payload: value,
    });
};

export const saveUpdatedSummaryEditor = (value) => dispatch => {
    dispatch({
        type: JD_JOB_SUMMARY_EDITOR_TEXT_HANDLER,
        payload: value,
    });
};

export const saveUpdatedSummaryAsHTML = (value) => dispatch => {
    dispatch({
        type: JD_JOB_SUMMARY_EDITOR_TEXT_AS_HTML_HANDLER,
        payload: value,
    });
};

export const loginandSubmitTemplateHandler = (value, templateId, selectedSkills, selectedLanguages, JOindustryField, JOfunctionField, rolesResHTMLData, rawContentSummaryAsHTML) => dispatch => {
    let resObj = {};
    let keys_needed = ["access_token", "userText", "userType"];

    dispatch(handleLoadingSpinnerOpenWithOptions(true, "Signing In", "large"));
    instance.post(BASE_URL_SQL + 'Login', value)
        .then(res => {
            if (res.data.statusCode === 404) {
                dispatch({
                    type: LOGIN_PAGE_ERROR_HANDLER,
                    payload: res.data.error,
                });
            }
            dispatch(handleLoadingSpinnerClose());
            keys_needed.map(item => {
                resObj[reverseString(item)] = res.data[item];
                return item;
            });
            resObj[reverseString("user_id")] = res.data.userId;
            resObj[reverseString("nameTitle")] = res.data.userText ? res.data.userText : res.data.user_name.toUpperCase();
            localStorage.setItem(reverseString("app_store"), JSON.stringify(resObj));
            dispatch({
                type: LOGIN_SUBMIT_FORM_HANDLER,
                payload: res.data
            });

            dispatch({
                type: JD_TEMPLATE_SAVE_SUMMARY_SHOW_LOGIN_POPUP_HANDLER,
                payload: false,
            });

            let jobCode = res.data.jobCode;
            ls.set('jobCode', jobCode);

            let userId = res.data.userId;
            // ls.set('userId', userId);

            let savedTempId = ls.get('suggestedTempId')
            savedTempId = templateId ? templateId : savedTempId

            let selected_skills = selectedSkills ? selectedSkills.map(item => {
                return {
                    _id: item.id,
                    level: item.level,
                    importance: item.importance,
                    title: item.title
                }
            }) : []

            let selected_languages = selectedLanguages ? selectedLanguages.map(item => {
                return {
                    _id: item.id,
                    level: item.level,
                    importance: item.importance,
                    title: item.title
                }
            }) : []

            let JDIndustry = JOindustryField ? JOindustryField[0].id : ""
            let JDFunction = JOfunctionField ? JOfunctionField[0].id : ""
            var experience = {
                "min_exp": state.JOBOPENING.forms.jobOpeningForm.fields.minYearField.value,
                "max_exp": state.JOBOPENING.forms.jobOpeningForm.fields.maxYearField.value
            }


            let salary_range = {
                min_sal: null,
                max_sal: null
            };
            if (state.JOBOPENING.forms.jobOpeningForm.fields.salaryTypeField.value === "yearly") {

                let min_max = state.JOBOPENING.forms.jobOpeningForm.fields.yearlySalaryRangField[0].value.split('-');
                salary_range.min_sal = min_max[0];
                salary_range.max_sal = min_max[1];

            }
            else if (state.JOBOPENING.forms.jobOpeningForm.fields.salaryTypeField.value === "hourly") {
                let min_max = state.JOBOPENING.forms.jobOpeningForm.fields.hourlyMaxRateField[0].value.split('-');
                salary_range.min_sal = min_max[0];
                salary_range.max_sal = min_max[1];
            }
            else if (state.JOBOPENING.forms.jobOpeningForm.fields.salaryTypeField.value === "monthly") {

                let min_max = state.JOBOPENING.forms.jobOpeningForm.fields.monthlySalaryRangField[0].value.split('-');
                salary_range.min_sal = parseInt(min_max[0]);
                salary_range.max_sal = parseInt(min_max[1]);

            }

            var rolesDataFilter = state.JOBOPENING.data.rolesData.filter((item) => {
                return item.isActive == true
            }).map(item => item.id)

            var resDataFilter = state.JOBOPENING.data.resData.filter((item) => {
                return item.isActive == true
            }).map(item => item.id)


            //sending call to save object
            let dataObj = {
                user_id: res.data.userId ? res.data.userId : userId,
                job_code: jobCode,
                template_id: savedTempId,
                title:state.JOBOPENING.forms.jobOpeningForm.fields.jobTitleField.value, //missing
                _selected_rr: {
                    htmlstring: state.JOBOPENING.data.rolesResHTMLData,
                    rol: rolesDataFilter,
                    res: resDataFilter
                },
                _selected_skills: [...selected_skills],
                _selected_languages: [...selected_languages],
                exp: experience,
                _selected_summary:
                {
                    htmlstring: rawContentSummaryAsHTML,
                    s_id: ["5fbcf9c331d5edc5c9ae2a39", "5fbcf9c331d5edc5c9ae2a39"]
                }
                ,
                _selected_industry: JDIndustry,
                _selected_function: JDFunction,
                employment_type: state.JOBOPENING.forms.jobOpeningForm.fields.employmenTypeField.value,
                positions: state.JOBOPENING.forms.jobOpeningForm.fields.positionField.value,
                salary_detail: {
                    amount: salary_range,
                    type: state.JOBOPENING.forms.jobOpeningForm.fields.salaryTypeField,
                    currency: "$",
                    visible: true
                },
                remote_work: state.JOBOPENING.forms.jobOpeningForm.fields.remoteWorkingField.value
            }

            dispatch(handleLoadingSpinnerOpenWithOptions(true, "Saving JD", "large"));
            instance.post(BASE_URL + 'Templates', dataObj)
                .then(res => {
                    dispatch(handleLoadingSpinnerClose());
                    // window.location.replace('/create/advertise')
                })
                .catch(error => {
                    dispatch(handleLoadingSpinnerClose());
                })

        })
        .catch(error => {
            dispatch(handleLoadingSpinnerClose());
        })
}

