import React from 'react';
import Divider from '@material-ui/core/Divider';

function DividerControl() {
    return <Divider orientation="vertical" flexItem />;
}

export default DividerControl;
import React from 'react';
import { EditorContext } from '../index';

export default function useEditor() {
    return React.useContext(EditorContext);
}
import React from 'react';
import useEditor from './useEditor';

export default function useEditorFocus() {
    const editor = useEditor();
    const [changesCount, setChangesCount] = React.useState(0);

    React.useEffect(() => {
        if (changesCount > 0) editor.ref.focus();
    }, [changesCount, editor.ref]);

    return () => {
        setChangesCount((currentChangesCount) => currentChangesCount + 1);
    };
}
import React from 'react';
import PropTypes from 'prop-types';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import Badge from '@material-ui/core/Badge';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
    badge: (props) => ({
        background: props.badgeColor,
    }),
});

function ButtonControl({
    children,
    onClick,
    disabled = false,
    active = false,
    text = '',
    badgeColor = null,
    ...rest
}) {
    const classes = useStyles({ badgeColor });
    return (
        <Tooltip title={text} aria-disabled={disabled}>
            <span>
                <IconButton
                    onClick={onClick}
                    disabled={disabled}
                    color={active ? 'primary' : 'default'}
                    {...rest}>
                    <Badge
                        anchorOrigin={{
                            vertical: 'top',
                            horizontal: 'left',
                        }}
                        classes={{ badge: classes.badge }}
                        overlap="circle"
                        badgeContent=" "
                        invisible={badgeColor === null}
                        variant="dot">
                        {children}
                    </Badge>
                </IconButton>
            </span>
        </Tooltip>
    );
}

ButtonControl.propTypes = {
    children: PropTypes.any.isRequired,
    onClick: PropTypes.func.isRequired,
    disabled: PropTypes.bool,
    text: PropTypes.any,
    badgeColor: PropTypes.any,
    active: PropTypes.bool,
};

export default ButtonControl;
import React from 'react';
import useEditor from '../../../hooks/useEditor';
import useEditorFocus from '../../../hooks/useEditorFocus';
import { EditorState } from 'draft-js';
import ButtonControl from '../core/ButtonControl';
import UndoIcon from '@material-ui/icons/Undo';

function UndoControl() {
    const editor = useEditor();
    const editorFocus = useEditorFocus();

    const onClick = () => {
        editor.onChange(EditorState.undo(editor.editorState));
        editorFocus();
    };

    return (
        <ButtonControl onClick={onClick} text={editor.translate('controls.undo.title')}>
            <UndoIcon />
        </ButtonControl>
    );
}

export default UndoControl;
import React from 'react';
import useEditor from '../../../hooks/useEditor';
import useEditorFocus from '../../../hooks/useEditorFocus';
import { EditorState } from 'draft-js';
import ButtonControl from '../core/ButtonControl';
import RedoIcon from '@material-ui/icons/Redo';

function RedoControl() {
    const editor = useEditor();
    const editorFocus = useEditorFocus();

    const onClick = () => {
        editor.onChange(EditorState.redo(editor.editorState));
        editorFocus();
    };

    return (
        <ButtonControl onClick={onClick} text={editor.translate('controls.redo.title')}>
            <RedoIcon />
        </ButtonControl>
    );
}

export default RedoControl;
import { EditorState, Modifier, RichUtils } from 'draft-js';

export const toggleMappedInlineStyle = (editorState, styleKeys, newInlineStyle) => {
    const selection = editorState.getSelection();

    // Turn off the other mapped inline styled in selection
    const newContentState = styleKeys.reduce(
        (contentState, inlineStyle) =>
            Modifier.removeInlineStyle(contentState, selection, inlineStyle),
        editorState.getCurrentContent()
    );

    let newEditorState = EditorState.push(editorState, newContentState, 'change-inline-style');

    const currentStyle = editorState.getCurrentInlineStyle();

    if (selection.isCollapsed()) {
        newEditorState = currentStyle.reduce((state, inlineStyle) => {
            return RichUtils.toggleInlineStyle(state, inlineStyle);
        }, newEditorState);
    }
    // If the inline style is being toggled on, apply it.
    if (!currentStyle.has(newInlineStyle)) {
        newEditorState = RichUtils.toggleInlineStyle(newEditorState, newInlineStyle);
    }

    return newEditorState;
};

export const getCurrentMappedInlineStyle = (editorState, styleKeys, defaultInlineStyle = null) => {
    const currentStyle = editorState.getCurrentInlineStyle();
    return (
        currentStyle.find((inlineStyle) => styleKeys.includes(inlineStyle)) || defaultInlineStyle
    );
};

export const hasAllSelectionTheInlineStyle = (editorState, inlineStyle) => {
    const selection = editorState.getSelection();
    const startKey = selection.getStartKey();
    const startOffset = selection.getStartOffset();
    const endKey = selection.getEndKey();
    const endOffset = selection.getEndOffset();

    const currentContent = editorState.getCurrentContent();

    let block = currentContent.getBlockForKey(startKey);
    let allHasTheInlineStyle = true;

    const styleRangesCallback = (block) => (start, end) => {
        const expectedStart = block.getKey() === startKey ? startOffset : 0;
        const expectedEnd = block.getKey() === endKey ? endOffset : block.getLength() - 1;
        allHasTheInlineStyle = start <= expectedStart && end >= expectedEnd;
    };

    while (block && allHasTheInlineStyle) {
        allHasTheInlineStyle = false;
        block.findStyleRanges(
            (character) => character.hasStyle(inlineStyle),
            styleRangesCallback(block)
        );
        if (block.getKey() !== endKey) break;
        block = currentContent.getBlockAfter(block.getKey());
    }

    return allHasTheInlineStyle;
};

export const getCurrentBlockType = (editorState, availableBlockTypes) => {
    const blockType = RichUtils.getCurrentBlockType(editorState);
    if (availableBlockTypes.find((avBlockType) => avBlockType === blockType)) return blockType;
    return 'default';
};

export const applyEntityToCurrentSelection = (editorState, entityType, mutability, entityData) => {
    const content = editorState.getCurrentContent();
    const contentWithEntity = content.createEntity(entityType, mutability, entityData);
    const entityKey = contentWithEntity.getLastCreatedEntityKey();
    const selection = editorState.getSelection();
    const contentStateWithEntity = Modifier.applyEntity(contentWithEntity, selection, entityKey);
    return EditorState.push(editorState, contentStateWithEntity, 'apply-entity');
};
import React from 'react';
import PropTypes from 'prop-types';
import useEditor from '../../../hooks/useEditor';
import useEditorFocus from '../../../hooks/useEditorFocus';
import { RichUtils } from 'draft-js';
import ButtonControl from './ButtonControl';
import { hasAllSelectionTheInlineStyle } from '../../../utils/editorStateUtils';

ToggleInlineStyleButtonControl.propTypes = {
    inlineStyle: PropTypes.string.isRequired,
    children: PropTypes.any.isRequired,
    text: PropTypes.any,
};

function ToggleInlineStyleButtonControl({ inlineStyle, children, text }) {
    const editor = useEditor();
    const editorFocus = useEditorFocus();
    const [isActive, setIsActive] = React.useState(false);

    React.useEffect(() => {
        setIsActive(hasAllSelectionTheInlineStyle(editor.editorState, inlineStyle));
    }, [editor.editorState, inlineStyle]);

    const onClick = () => {
        const newEditorState = RichUtils.toggleInlineStyle(editor.editorState, inlineStyle);
        editor.onChange(newEditorState);
        editorFocus();
    };

    return (
        <ButtonControl
            text={text}
            onClick={onClick}
            active={isActive}
            disabled={editor.editorState.getSelection().isCollapsed()}>
            {children}
        </ButtonControl>
    );
}

export default ToggleInlineStyleButtonControl;
export default {
    BOLD: 'BOLD',
    ITALIC: 'ITALIC',
    UNDERLINE: 'UNDERLINE',
    STRIKETHROUGH: 'STRIKETHROUGH',
    CODE: 'CODE',
    FONT_FAMILY: 'FONT_FAMILY',
    FONT_SIZE: 'FONT_SIZE',
    FONT_COLOR: 'FONT_COLOR',
    FONT_BACKGROUND: 'FONT_BACKGROUND',
};
import React from 'react';
import useEditor from '../../../hooks/useEditor';
import ToggleInlineStyleButtonControl from '../core/ToggleInlineStyleButtonControl';
import FormatBoldIcon from '@material-ui/icons/FormatBold';
import inlineStyles from '../../../types/inlineStyles';

function BoldControl() {
    const editor = useEditor();

    return (
        <ToggleInlineStyleButtonControl
            inlineStyle={inlineStyles.BOLD}
            text={editor.translate('controls.bold.title')}>
            <FormatBoldIcon />
        </ToggleInlineStyleButtonControl>
    );
}

export default BoldControl;
import React from 'react';
import useEditor from '../../../hooks/useEditor';
import ToggleInlineStyleButtonControl from '../core/ToggleInlineStyleButtonControl';
import FormatItalicIcon from '@material-ui/icons/FormatItalic';
import inlineStyles from '../../../types/inlineStyles';

function ItalicControl() {
    const editor = useEditor();

    return (
        <ToggleInlineStyleButtonControl
            inlineStyle={inlineStyles.ITALIC}
            text={editor.translate('controls.italic.title')}>
            <FormatItalicIcon />
        </ToggleInlineStyleButtonControl>
    );
}

export default ItalicControl;
import React from 'react';
