// Created by Andreas Wik
export const isLightOrDark = (color) => {
    let r, g, b;
    // Check the format of the color, HEX or RGB?
    if (color.match(/^rgb/)) {
        // If HEX --> store the red, green, blue values in separate variables
        color = color.match(/^rgba?\((\d+),\s*(\d+),\s*(\d+)(?:,\s*(\d+(?:\.\d+)?))?\)$/);

        r = color[1];
        g = color[2];
        b = color[3];
    } else {
        // If RGB --> Convert it to HEX: http://gist.github.com/983661
        color = +('0x' + color.slice(1).replace(color.length < 5 && /./g, '$&$&'));

        r = color >> 16;
        g = (color >> 8) & 255;
        b = color & 255;
    }

    // HSP (Highly Sensitive Poo) equation from http://alienryderflex.com/hsp.html
    const hsp = Math.sqrt(0.299 * (r * r) + 0.587 * (g * g) + 0.114 * (b * b));

    // Using the HSP value, determine whether the color is light or dark
    if (hsp > 127.5) {
        return 'light';
    } else {
        return 'dark';
    }
};
import React from 'react';
import PropTypes from 'prop-types';
import ButtonControl from '../ButtonControl';
import Popover from '@material-ui/core/Popover';
import Tooltip from '@material-ui/core/Tooltip';
import FormatColorResetIcon from '@material-ui/icons/FormatColorReset';
import CheckIcon from '@material-ui/icons/Check';
import { isLightOrDark } from '../../../../utils/colorUtils';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
    colorRow: {
        display: 'flex',
        alignContent: 'center',
        alignItems: 'center',
        padding: 5,
    },
    colorItem: {
        height: 25,
        width: 25,
        borderRadius: '50%',
        margin: 3,
        border: 'solid 1px #c4c4c4',
        cursor: 'pointer',
        display: 'flex',
        alignContent: 'center',
        alignItems: 'center',
    },
});

function ColorSelectorControl({
    selectedColor,
    onSelectColor,
    colors,
    colorsPerRow = 10,
    children,
    ...rest
}) {
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const menuId = Math.random().toString(36).substring(8);
    const colorRows = [[]];
    for (let i = 0, rowI = 0; i < colors.length; i++) {
        if (i % colorsPerRow === 0) {
            rowI++;
            colorRows[rowI] = [];
        }
        colorRows[rowI].push(colors[i]);
    }

    const handleOpen = (ev) => {
        setAnchorEl(ev.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    return (
        <React.Fragment>
            <ButtonControl
                onClick={handleOpen}
                aria-controls={menuId}
                aria-haspopup="true"
                badgeColor={selectedColor ? selectedColor.color : null}
                {...rest}>
                {children}
            </ButtonControl>
            <Popover
                id={menuId}
                anchorEl={anchorEl}
                anchorOrigin={{
                    vertical: 'bottom',
                    horizontal: 'left',
                }}
                transformOrigin={{
                    vertical: 'top',
                    horizontal: 'left',
                }}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}>
                <React.Fragment>
                    <Button
                        startIcon={<FormatColorResetIcon />}
                        fullWidth
                        color="secondary"
                        onClick={() => {
                            handleClose();
                            onSelectColor(null);
                        }}>
                        None
                    </Button>
                    {colorRows.map((colorRow, colorRowI) => (
                        <div key={`color-row-${colorRowI}`} className={classes.colorRow}>
                            {colorRow.map((colorData) => (
                                <Tooltip key={colorData.value} title={colorData.text}>
                                    <div
                                        onClick={() => {
                                            handleClose();
                                            onSelectColor(colorData);
                                        }}
                                        className={classes.colorItem}
                                        style={{
                                            backgroundColor: colorData.color,
                                        }}>
                                        {selectedColor &&
                                        colorData.value === selectedColor.value ? (
                                            <CheckIcon
                                                style={{
                                                    color:
                                                        isLightOrDark(colorData.color) === 'dark'
                                                            ? '#fff'
                                                            : '#000',
                                                }}
                                            />
                                        ) : null}
                                    </div>
                                </Tooltip>
                            ))}
                        </div>
                    ))}
                </React.Fragment>
            </Popover>
        </React.Fragment>
    );
}

ColorSelectorControl.propTypes = {
    selectedColor: PropTypes.object,
    onSelectColor: PropTypes.func.isRequired,
    colors: PropTypes.array.isRequired,
    colorsPerRow: PropTypes.number,
    children: PropTypes.any,
};

