import { connect } from "react-redux";
import AddCollaboration from "./component";
import {
    textFieldChangeHandler,
    saveAddedCollaborators
} from "../actions";
import ls from 'local-storage';

const mapStateToProps = state => ({
    EmailField: state.ADD_COLLABORATOR.forms.addCollaboratorForm.fields.EmailField,
    NameField: state.ADD_COLLABORATOR.forms.addCollaboratorForm.fields.NameField,
    addedCollaborator: state.ADD_COLLABORATOR.data.addedCollaborator,
    jobCode :state.JOBOPENING.forms.jobOpeningForm.fields.jobCodeField,
    jobTitle:state.JOBOPENING.forms.jobOpeningForm.fields.jobTitleField,

})

const mapDispatchToProps = (dispatch, props) => ({
    textFieldChangeHandler: (name, e) => {
        if (typeof e.value !== "string") {
            e.value = "";
        }
        dispatch(textFieldChangeHandler(name, e));
    },
    sendCollaboration: () => {
        dispatch(handleLoadingSpinnerOpenWithOptions(true, "Sending Invite...", "large"));

        let data = {
            Collab_email: this.stateProps.EmailField,
            Collab_Name: this.stateProps.NameField,
            Job_Title: this.stateProps.jobTitle,
            Job_Code: this.stateProps.jobCode,
        }

        instance.post(BASE_URL_COL+'User/SendCollabInvite', data)
        .then(res => {
            dispatch(handleLoadingSpinnerClose());
        })
        .catch(error => {
            dispatch(handleLoadingSpinnerClose());
        })
    }

})

const mergeProps = (stateProps, dispatchProps, ownProps) => ({
    ...stateProps,
    ...dispatchProps,
    addCollaboratorHandler: () => {
        dispatchProps.addCollaboratorHandler(stateProps.fields.EmailField.value, stateProps.fields.NameField.value)
    },
    ...ownProps,
})

export default connect(
    mapStateToProps,
    mapDispatchToProps,
    mergeProps
)(AddCollaboration);
