import React, { Component } from 'react';
import { styles } from "./styles";
import { withStyles } from "@material-ui/core/styles";
import { Button } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import JobOpeningMetaFields from "./MetaFields/container";
import { ValidatorForm } from "react-material-ui-form-validator";
import { NavLink, withRouter } from "react-router-dom";
import AlertDialog from "../../../common/ReduxBased/confirmationDialog/container";
import Login from "../../../Login/container";
import CollaboratorAndPreview from "../CollaboratorAndPreview/container";
import { getAppStoreValue } from "../../../common/Helpers";
import ls from 'local-storage';

class JobOpening extends Component {

    componentDidMount = () => {
        const userId = ls.get('userId');
        const userName = getAppStoreValue('nameTitle')
        let isLogin = userId && userName ? true : false
        if (isLogin) {
            this.props.getJobCode();
        }
        this.props.fetchDataSuggestedTemplate();
    }

    SubmitHandler = () => {
        this.props.history.push('/create/info');
        this.props.headerPathActiveHandler(0);
    }



    saveJobOpening =() =>{
        

        var experience = {...this.props.minYearField, ...this.props.maxYearField } 
        var rolesDataFilter = this.props.rolesData.filter((item)=>{
            return item.isActive==true
        }).map(item=>item.id)

        var resDataFilter = this.props.resData.filter((item)=>{
            return item.isActive==true
        }).map(item=>item.id)
        let data={

            job_code:this.props.jobCodeField,
            template_id:this.templateId,
            title:this.props.jobTitle,
            _selected_rr :{
                htmlstring:this.props.rolesResHTMLData,
                rol:rolesDataFilter,
                res:resDataFilter
            },
            salary_detail:{
                amount:this.props.showSalaryField,
                type:this.props.salaryTypeField,
                currency:'',
                visible:''
            },
            exp:experience,


            _selected_industry:this.props.industryField,
            _selected_function:this.props.functionField,
            employment_type:this.props.employmenTypeField,
            positions:this.props.positionField,
            remote_work:this.props.remoteWorkingField, 

            // minYearField:this.props.minYearField,
            // maxYearField:this.props.maxYearField,
            
       
            hourlyMaxRateField:this.props.hourlyMaxRateField,
            monthlySalaryRangField:this.props.monthlySalaryRangField,
            monthlyCurrencyField:this.props.monthlyCurrencyField,
            yearlySalaryRangField:this.props.yearlySalaryRangField,
            yearlyCurrencyField:this.props.yearlyCurrencyField,

            htmlstring:this.props.rolesResHTMLData,
            rol:this.props.rolesData,
            res:this.props.resData,
            

        }
     
    console.log(data)

     
        // axios.post(BASE_URL+'', data)
    
    }
    render() {
        const {
            classes,
            headerPathActiveHandler,
            isLogin
        } = this.props;

        return (
            <>
                <div className={classes.jobopeningwrapper}>

                    <CollaboratorAndPreview />

                    <ValidatorForm
                        onSubmit={this.SubmitHandler}
                    // autoComplete="off"
                    >
                        <JobOpeningMetaFields />

                        {/* <NavLink
                            to="/create/info"
                            className={classes.jdbuildingnextbtn}
                            onClick={() => headerPathActiveHandler(0)}
                        > */}
                        {isLogin  ?
                            <Button variant="contained" color="primary" onClick={this.saveJobOpening}>
                                Save & Next
                            </Button>
                            :
                            <Button variant="contained" color="primary" type="submit">
                                Next
                            </Button>
                        }
                        {/* </NavLink> */}
                    </ValidatorForm>
                </div>
            </>

        )
    }
}

export default withStyles(styles)(withRouter(JobOpening));
