import {
    JD_COLLABORATOR_DATA,
    JD_JOB_OPENING_TEXT_CHANGE_HANDLER,
    JD_ADDITIPONAL_INFO_TEXT_CHANGE_HANDLER,
    JD_SUMMARY_TEXT_CHANGE_HANDLER,


} from "./actions";

export const collaboratorReducer = (state = state.JOBOPENING, action) => {
    
    switch (action.type) {
        case JD_JOB_OPENING_TEXT_CHANGE_HANDLER: {
            return {
                ...state.JOBOPENING,
                forms: {
                    ...state.forms,
                    jobOpeningForm: {
                        ...state.forms.jobOpeningForm,
                        fields: {
                            ...state.forms.jobOpeningForm.fields,
                            [action.key]: {
                                ...state.JOBOPENING.forms.jobOpeningForm.fields[action.key],
                                value: action.payload
                            }
                        }
                    }
                }
            }
        }

        default: {
            return {
                ...state
            };
        }
    }
}
