import { connect } from "react-redux";
import AddCollaboration from "./component";
import {
    textFieldChangeHandler,
    saveAddedCollaborators
} from "../actions";
import ls from 'local-storage';

const mapStateToProps = state => ({
    fields: state.ADD_COLLABORATOR.forms.addCollaboratorForm.fields,
    addedCollaborator: state.ADD_COLLABORATOR.data.addedCollaborator,
    JDB :state.JOBOPENING.forms.jobOpeningForm.jobCodeField,
    Job_Title: state.JOBOPENING.forms.jobOpeningForm.jobTitleField,
})

const mapDispatchToProps = (dispatch, props) => ({
    textFieldChangeHandler: (name, e) => {
        if (typeof e.value !== "string") {
            e.value = "";
        }
        dispatch(textFieldChangeHandler(name, e));
    },
    addCollaboratorHandler: (EmailField, NameField) => {

        let jobCode = ls.get('jobCode');

        let data = {
            Collab_email: EmailField,
            Collab_Name: NameField,
            Job_Title: Job_Title,
            Job_Code: jobCode
        }
        dispatch(saveAddedCollaborators(data))
    }
})

const mergeProps = (stateProps, dispatchProps, ownProps) => ({
    ...stateProps,
    ...dispatchProps,
    addCollaboratorHandler: () => {
        dispatchProps.addCollaboratorHandler(stateProps.fields.EmailField.value, stateProps.fields.NameField.value)
    },
    ...ownProps,
})

export default connect(
    mapStateToProps,
    mapDispatchToProps,
    mergeProps
)(AddCollaboration);
